mapper.select:
  		SELECT

		FROM
			user_info_t
		WHERE
			1 = 1
		AND (
		    1!=1

				OR account = #{account,jdbcType=VARCHAR}


			OR telephone = #{telephone,jdbcType=VARCHAR}
			OR email = #{email,jdbcType=VARCHAR}
			OR qq_account = #{qqAccount,jdbcType=VARCHAR}
		)
		AND is_cancelled = "1"
		AND is_forbidden = "1"
